import React, { useState } from 'react';
import './App.css';

function App() {
  const [resume, setResume] = useState(null);
  const [jd, setJd] = useState(null);
  const [result, setResult] = useState('');

  const handleResumeChange = (e) => {
    setResume(e.target.files[0]);
  };

  const handleJdChange = (e) => {
    setJd(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!resume || !jd) {
      alert('Please upload both resume and job description files.');
      return;
    }

    const formData = new FormData();
    formData.append('resume', resume);
    formData.append('jd', jd);

    try {
      const response = await fetch('http://localhost:8000/score', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();
      setResult(`Semantic Match Score: ${data.score}%`);
    } catch (error) {
      console.error('Error:', error);
      setResult('Something went wrong. Try again later.');
    }
  };

  return (
    <div className="App">
      <h1>JobQuest âœ¨</h1>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Upload Resume:</label>
          <input type="file" onChange={handleResumeChange} />
        </div>

        <div>
          <label>Upload Job Description:</label>
          <input type="file" onChange={handleJdChange} />
        </div>

        <button type="submit">Submit</button>
      </form>

      {result && <p>{result}</p>}
    </div>
  );
}

export default App;
