import React, { useState } from 'react';
import './App.css';

function App() {
  const [resume, setResume] = useState(null);
  const [jd, setJd] = useState(null);
  const [result, setResult] = useState('');
  const [resumeFileName, setResumeFileName] = useState('');
  const [jdFileName, setJdFileName] = useState('');
  const [companyName, setCompanyName] = useState('');
  const [jobTitle, setJobTitle] = useState("");

  const handleResumeChange = (e) => {
   setResume(e.target.files[0]);
   setResumeFileName(e.target.files[0].name);
  };

  const handleJdChange = (e) => {
    setJd(e.target.files[0]);
    setJdFileName(e.target.files[0].name);
    const nameWithoutExt = e.target.files[0].name.split('.')[0];
    setCompanyName(nameWithoutExt.charAt(0).toUpperCase() + nameWithoutExt.slice(1));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!resume || !jd) {
      alert('Please upload both resume and job description files.');
      return;
    }

    const formData = new FormData();
    formData.append('resume', resume);
    formData.append('jd', jd);
    formData.append('company', companyName);
    formData.append('role', jobTitle);

    try {
      const response = await fetch('http://localhost:8000/score', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();
      setResult(
  `‚úÖ Company: ${companyName} | Role: ${jobTitle}\nüìÑ Resume: ${resumeFileName}\nüìù JD: ${jdFileName}\nüìä Match Score: ${data.score.toFixed(2)}%`
);
    } catch (error) {
      console.error('Error:', error);
      setResult('Something went wrong. Try again later.');
    }
  };

  return (
    <div className="App">
      <h1>JobQuest ‚ú®</h1>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Upload Resume:</label>
          <input type="file" onChange={handleResumeChange} />
        </div>

        <div>
          <label>Upload Job Description:</label>
          <input type="file" onChange={handleJdChange} />
        </div>
        <div>
           <label>Company Name:</label>
            <input
              type="text"
              value={companyName}
               onChange={(e) => setCompanyName(e.target.value)}
            />
        </div>

         <div>
         <label>Job Title:</label>
         <input
           type="text"
           value={jobTitle}
           onChange={(e) => setJobTitle(e.target.value)}
           />
        </div>

        <button type="submit">Submit</button>
      </form>

      {result && <pre>{result}</pre>}
    </div>
  );
}

export default App;
